import React from 'react';

const employees = ['Alice', 'Bob', 'Carol', 'Dave', 'Eve'];

function getDaysInMonth(year, month) {
  return new Date(year, month + 1, 0).getDate();
}

function getMonthMatrix(year, month) {
  const daysInMonth = getDaysInMonth(year, month);
  const firstDay = new Date(year, month, 1).getDay();
  const matrix = [];
  let week = Array(firstDay).fill(null);
  for (let day = 1; day <= daysInMonth; day++) {
    week.push(day);
    if (week.length === 7) {
      matrix.push(week);
      week = [];
    }
  }
  if (week.length) {
    while (week.length < 7) week.push(null);
    matrix.push(week);
  }
  return matrix;
}

function FrontDesk() {
  const today = new Date();
  const year = today.getFullYear();
  const month = today.getMonth();
  const monthName = today.toLocaleString('default', { month: 'long' });
  const daysMatrix = getMonthMatrix(year, month);

  // Assign employees in a rotating schedule
  let empIdx = 0;
  const assignments = [];
  for (let day = 1; day <= getDaysInMonth(year, month); day++) {
    assignments[day] = employees[empIdx % employees.length];
    empIdx++;
  }

  const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold mb-6">Front Desk Duty Calendar</h2>
      <div className="max-w-3xl mx-auto bg-[#232f47] rounded-lg shadow-lg p-6">
        <div className="flex justify-between items-center mb-4">
          <span className="text-xl font-semibold">{monthName} {year}</span>
        </div>
        <div className="grid grid-cols-7 gap-2 text-center mb-2">
          {weekDays.map(d => (
            <div key={d} className="font-semibold text-blue-300">{d}</div>
          ))}
        </div>
        <div className="grid grid-cols-7 gap-2">
          {daysMatrix.flat().map((day, idx) => (
            <div
              key={idx}
              className={`h-20 rounded flex flex-col items-center justify-center ${day ? 'bg-[#1a253a] text-white' : ''}`}
              style={{ opacity: day ? 1 : 0 }}
            >
              {day && (
                <>
                  <div className="text-lg font-bold">{day}</div>
                  <div className="mt-2 text-xs bg-blue-600 rounded px-2 py-1 text-white">
                    {assignments[day]}
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
        <div className="mt-6">
          <h3 className="font-semibold mb-2 text-blue-300">Employee Legend:</h3>
          <div className="flex gap-4 flex-wrap">
            {employees.map(e => (
              <span key={e} className="bg-blue-600 text-white px-3 py-1 rounded text-sm">{e}</span>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default FrontDesk;
