import { createClient } from '@supabase/supabase-js'

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY

export const supabase = createClient(supabaseUrl, supabaseAnonKey)

// Helper functions for rooms management
export const roomsApi = {
  // Get all rooms (including archived for admin)
  async getAllRooms() {
    try {
      const response = await fetch('http://localhost:4000/api/rooms')
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }
      const data = await response.json()
      return data
    } catch (error) {
      console.error('Error fetching rooms:', error)
      throw error
    }
  },

  // Get room by ID
  async getRoomById(id) {
    try {
      const response = await fetch(`http://localhost:4000/api/rooms/${id}`)
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }
      const data = await response.json()
      return data
    } catch (error) {
      console.error('Error fetching room:', error)
      throw error
    }
  },

  // Create new room
  async createRoom(roomData) {
    try {
      const response = await fetch('http://localhost:4000/api/rooms', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(roomData)
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(errorData.error || `HTTP error! status: ${response.status}`)
      }

      const data = await response.json()
      return data.room
    } catch (error) {
      console.error('Error creating room:', error)
      throw error
    }
  },

  // Update room
  async updateRoom(id, roomData) {
    try {
      const response = await fetch(`http://localhost:4000/api/rooms/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(roomData)
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(errorData.error || `HTTP error! status: ${response.status}`)
      }

      const data = await response.json()
      return data.room
    } catch (error) {
      console.error('Error updating room:', error)
      throw error
    }
  },

  // Delete room
  async deleteRoom(id) {
    try {
      const response = await fetch(`http://localhost:4000/api/rooms/${id}`, {
        method: 'DELETE'
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(errorData.error || `HTTP error! status: ${response.status}`)
      }

      const data = await response.json()
      return data.room
    } catch (error) {
      console.error('Error deleting room:', error)
      throw error
    }
  },

  // Update room status
  async updateRoomStatus(id, status) {
    try {
      const response = await fetch(`http://localhost:4000/api/rooms/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status })
      })

      if (!response.ok) {
        const errorData = await response.json()
        throw new Error(errorData.error || `HTTP error! status: ${response.status}`)
      }

      const data = await response.json()
      return data.room
    } catch (error) {
      console.error('Error updating room status:', error)
      throw error
    }
  },

  // Search rooms
  async searchRooms(searchTerm) {
    try {
      const response = await fetch(`http://localhost:4000/api/rooms?search=${encodeURIComponent(searchTerm)}`)
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }
      const data = await response.json()
      return data
    } catch (error) {
      console.error('Error searching rooms:', error)
      throw error
    }
  }
}

// Dashboard API functions
export const dashboardApi = {
  // Get dashboard statistics
  async getDashboardStats() {
    try {
      console.log('Fetching dashboard stats from: http://localhost:4000/api/dashboard/stats')
      const response = await fetch('http://localhost:4000/api/dashboard/stats')
      console.log('Dashboard stats response status:', response.status)
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }
      const data = await response.json()
      console.log('Dashboard stats data received:', data)
      return data
    } catch (error) {
      console.error('Error fetching dashboard stats:', error)
      throw error
    }
  },

  // Get dashboard rooms preview
  async getDashboardRooms() {
    try {
      const response = await fetch('http://localhost:4000/api/dashboard/rooms')
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }
      const data = await response.json()
      return data
    } catch (error) {
      console.error('Error fetching dashboard rooms:', error)
      throw error
    }
  }
}
