import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink, Navigate } from 'react-router-dom';
import Rooms from './Rooms.jsx';
import Guests from './Guests.jsx';
import FrontDesk from './FrontDesk';
import Bookings from './Bookings';
import Customers from './Customers.jsx';
import { dashboardApi, supabase, adminAuth } from './lib/supabase';
import Login from './Login.jsx';
import './Navbar.css';
import './Dashboard.css';

const sidebarItems = [
  { name: 'Dashboard', icon: 'ðŸ ', path: '/' },
  { name: 'Rooms', icon: 'ðŸ›ï¸', path: '/rooms' },
  { name: 'Guests', icon: 'ðŸ‘¤', path: '/guests' },
  { name: 'Customers', icon: 'ðŸ“‡', path: '/customers' },
  { name: 'Front Desk', icon: 'ðŸ›Žï¸', path: '/front-desk' },
  { name: 'Bookings', icon: 'ðŸ“–', path: '/bookings' },
];

function Dashboard() {
  const [stats, setStats] = useState({
    todayCheckIns: 0,
    todayCheckOuts: 0,
    totalAvailableRooms: 0,
    totalOccupiedRooms: 0,
    checkInChange: '+0',
    checkOutChange: '+0',
    availableChange: '0',
    occupiedChange: '0'
  });
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const [statsData, roomsData] = await Promise.all([
          dashboardApi.getDashboardStats(),
          dashboardApi.getDashboardRooms()
        ]);
        setStats(statsData);
        setRooms(roomsData);
        setError(null);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();

    // Refresh data every 30 seconds
    const interval = setInterval(fetchDashboardData, 30000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        </div>
      </main>
    );
  }

  if (error) {
    return (
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
        </div>
      </main>
    );
  }

  return (
    <main className="dashboard-main">
      {/* Overview Cards */}
      <section className="mb-8">
        <div className="section-header">
          <h2 className="section-title">Overview</h2>
          <button onClick={() => window.location.reload()} className="btn-refresh">ðŸ”„ Refresh</button>
        </div>
        <div className="overview-grid">
          {[
            { title: "Today's Check-in", value: stats.todayCheckIns, change: `${stats.checkInChange} from yesterday`, icon: 'âž¡ï¸' },
            { title: "Today's Check-out", value: stats.todayCheckOuts, change: `${stats.checkOutChange} from yesterday`, icon: 'âž¡ï¸' },
            { title: 'Total Available Rooms', value: stats.totalAvailableRooms, change: `${stats.availableChange} since yesterday`, icon: 'ðŸ ' },
            { title: 'Total Occupied Rooms', value: stats.totalOccupiedRooms, change: `${stats.occupiedChange} since yesterday`, icon: 'ðŸ ' },
            { title: 'Total Revenue', value: `â‚±${(stats.totalRevenue || 0).toLocaleString()}`, change: 'from confirmed bookings', icon: 'ðŸ’°' },
          ].map((card, idx) => (
            <div key={idx} className="overview-card">
              <div className="card-row">
                <span className="card-title">{card.title}</span>
                <span className="card-icon">{card.icon}</span>
              </div>
              <div className="card-value">{card.value}</div>
              <div className="card-change">{card.change}</div>
            </div>
          ))}
        </div>
      </section>
      {/* Rooms Section */}
      <section className="mb-8">
        <div className="section-header">
          <h2 className="section-title">Rooms</h2>
          <button className="btn-outline" onClick={() => window.location.href = '/rooms'}>View All Rooms</button>
        </div>
        <div className="rooms-grid">
          {/* Room Cards */}
          {rooms.length > 0 ? rooms.map((room, idx) => (
            <div key={room.id || idx} className="room-card">
              <img 
                src={room.images && room.images.length > 0 ? room.images[0] : 'https://via.placeholder.com/400x200?text=No+Image'} 
                alt={room.name} 
                className="room-image" 
              />
              <div className="room-body">
                <div className="room-name">{room.name}</div>
                <div className="room-occupied">{room.occupied} occupied</div>
                <div className="room-price">â‚±{room.price.toLocaleString()}/night</div>
                <div className="room-amenities">
                  {room.amenities && room.amenities.length > 0 ? room.amenities.map((amenity, i) => (
                    <span key={i} className="room-amenity">{amenity}</span>
                  )) : (
                    <span className="room-amenity muted">No amenities listed</span>
                  )}
                </div>
                <div className="room-actions">
                  <button className="btn-view" onClick={() => window.location.href = '/rooms'}>View Details</button>
                  <button className="btn-bookings" onClick={() => window.location.href = '/bookings'}>View Bookings</button>
                </div>
              </div>
            </div>
          )) : (
            <div className="col-span-full text-center py-8 text-gray-400">
              No rooms available. <button 
                className="text-blue-400 hover:text-blue-300 underline"
                onClick={() => window.location.href = '/rooms'}
              >
                Add your first room
              </button>
            </div>
          )}
        </div>
      </section>
      {/* Room Status */}
      <section>
        <h2 className="section-title mb-4">Room Status Overview</h2>
        <div className="status-panel">
          <div className="status-grid">
            <div className="status-item">
              <div className="status-available">{stats.totalAvailableRooms}</div>
              <div className="status-label">Available Rooms</div>
            </div>
            <div className="status-item">
              <div className="status-occupied">{stats.totalOccupiedRooms}</div>
              <div className="status-label">Occupied Rooms</div>
            </div>
            <div className="status-item">
              <div className="status-maint">
                {rooms.filter(room => room.status === 'maintenance').length}
              </div>
              <div className="status-label">Under Maintenance</div>
            </div>
          </div>
          <div className="status-updated">
            Last updated: {new Date().toLocaleTimeString()}
          </div>
        </div>
      </section>
    </main>
  );
}

function App() {
  const [notificationsOpen, setNotificationsOpen] = useState(false);
  const [profileOpen, setProfileOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const notifRef = useRef(null);
  const profileRef = useRef(null);
  const [lastSeenAt, setLastSeenAt] = useState(null);
  const [authed, setAuthed] = useState(!!adminAuth.getToken());
  const [adminUser, setAdminUser] = useState(() => adminAuth.getUser());

  useEffect(() => {
    // Listen for login/logout events and focus/storage to refresh auth state
    const onAuthChange = () => {
      setAuthed(!!adminAuth.getToken());
      setAdminUser(adminAuth.getUser());
    };
    window.addEventListener('admin-auth-changed', onAuthChange);
    window.addEventListener('storage', onAuthChange);
    window.addEventListener('focus', onAuthChange);
    return () => {
      window.removeEventListener('admin-auth-changed', onAuthChange);
      window.removeEventListener('storage', onAuthChange);
      window.removeEventListener('focus', onAuthChange);
    };
  }, []);

  useEffect(() => {
    if (!authed) return; // only after login
    // Preload latest reservation timestamp to avoid false notifications on load
    (async () => {
      try {
        // Fetch recent completed payments then enrich with reservation details
        const { data: payments, error } = await supabase
          .from('payments')
          .select('id, reservation_id, created_at, status')
          .eq('status', 'completed')
          .order('created_at', { ascending: false })
          .limit(5);
        if (error) {
          console.warn('Prefetch latest completed payments failed:', error.message);
        } else if (Array.isArray(payments) && payments.length > 0) {
          const ids = payments.map(p => p.reservation_id).filter(Boolean);
          let resMap = {};
          if (ids.length > 0) {
            const { data: reservations, error: resErr } = await supabase
              .from('reservations')
              .select('id, user_name, room_name, status')
              .in('id', ids);
            if (resErr) {
              console.warn('Prefetch reservation lookup failed:', resErr.message);
            } else if (Array.isArray(reservations)) {
              resMap = Object.fromEntries(reservations.map(r => [r.id, r]));
            }
          }
          const items = payments.map(p => ({
            id: p.id,
            user_name: resMap[p.reservation_id]?.user_name || 'Guest',
            room_name: resMap[p.reservation_id]?.room_name || 'Room',
            created_at: p.created_at,
            status: 'paid',
          }));
          setLastSeenAt(payments[0].created_at);
          setNotifications(items);
        }
      } catch (e) {
        console.warn('Prefetch latest payments error:', e);
      }
    })();

    // Subscribe to reservations changes
    const channel = supabase
      .channel('payments-notify')
      .on('postgres_changes', { event: '*', schema: 'public', table: 'payments' }, (payload) => {
        const p = payload.new || payload.record || {};
        console.log('[Realtime] payments change:', payload);
        if (!p || !p.id) return;
        // Only notify when a payment is completed
        if (p.status !== 'completed') return;
        (async () => {
          try {
            let user_name = 'Guest';
            let room_name = 'Room';
            if (p.reservation_id) {
              const { data: r, error: rErr } = await supabase
                .from('reservations')
                .select('id, user_name, room_name')
                .eq('id', p.reservation_id)
                .maybeSingle();
              if (!rErr && r) {
                user_name = r.user_name || user_name;
                room_name = r.room_name || room_name;
              }
            }
            const item = {
              id: p.id,
              user_name,
              room_name,
              created_at: p.created_at,
              status: 'paid',
            };
            setNotifications((prev) => [item, ...prev].slice(0, 10));
            setUnreadCount((c) => c + 1);
            if (p.created_at) setLastSeenAt(p.created_at);
          } catch (err) {
            console.warn('[Realtime] enrich payment failed:', err);
          }
        })();
      })
      .subscribe((status) => {
        console.log('[Realtime] channel status:', status);
      });

    return () => {
      try { supabase.removeChannel(channel); } catch (e) { /* noop */ }
    };
  }, [authed]);

  useEffect(() => {
    if (!authed) return; // only after login
    // Fallback polling every 5s if realtime is unavailable; pause when tab hidden
    let interval;
    const tick = async () => {
      try {
        const { data: p, error } = await supabase
          .from('payments')
          .select('id, reservation_id, created_at, status')
          .eq('status', 'completed')
          .order('created_at', { ascending: false })
          .limit(1)
          .maybeSingle();
        if (error) {
          console.warn('[Polling] latest payment error:', error.message);
          return;
        }
        if (p && p.created_at && (!lastSeenAt || new Date(p.created_at) > new Date(lastSeenAt))) {
          console.log('[Polling] detected completed payment:', p);
          let user_name = 'Guest';
          let room_name = 'Room';
          if (p.reservation_id) {
            const { data: r, error: rErr } = await supabase
              .from('reservations')
              .select('id, user_name, room_name')
              .eq('id', p.reservation_id)
              .maybeSingle();
            if (!rErr && r) {
              user_name = r.user_name || user_name;
              room_name = r.room_name || room_name;
            }
          }
          const item = {
            id: p.id,
            user_name,
            room_name,
            created_at: p.created_at,
            status: 'paid',
          };
          setNotifications((prev) => [item, ...prev].slice(0, 10));
          setUnreadCount((c) => c + 1);
          setLastSeenAt(p.created_at);
        }
      } catch (e) {
        console.warn('[Polling] exception:', e);
      }
    };
    const start = () => {
      clearInterval(interval);
      interval = setInterval(() => {
        if (document.visibilityState === 'visible') tick();
      }, 5000);
    };
    start();
    return () => clearInterval(interval);
  }, [lastSeenAt, authed]);

  useEffect(() => {
    // Close dropdowns on outside click
    const onClick = (e) => {
      if (notifRef.current && !notifRef.current.contains(e.target)) {
        setNotificationsOpen(false);
      }
      if (profileRef.current && !profileRef.current.contains(e.target)) {
        setProfileOpen(false);
      }
    };
    document.addEventListener('click', onClick);
    return () => document.removeEventListener('click', onClick);
  }, []);

  const toggleNotifications = () => {
    setNotificationsOpen((o) => !o);
    setUnreadCount(0);
    setProfileOpen(false);
  };

  const toggleProfile = () => {
    setProfileOpen((o) => !o);
    setNotificationsOpen(false);
  };

  const handleLogout = async () => {
    try {
      adminAuth.logout();
      window.location.href = '/login';
    } catch (e) {
      alert('Failed to logout');
    }
  };

  // If not authenticated, only allow access to /login
  if (!authed) {
    return (
      <Router>
        <Routes>
          <Route path="/login" element={<Login />} />
          <Route path="*" element={<Navigate to="/login" replace />} />
        </Routes>
      </Router>
    );
  }

  return (
    <Router>
      <div className="flex min-h-screen bg-[#151e2e] text-white">
        {/* Sidebar */}
        <aside className="sidebar">
          <div className="sidebar-logo">
            <div>
              <div className="sidebar-logo-title">GOLD ROCK</div>
              <div className="sidebar-logo-sub">INN</div>
            </div>
          </div>
          <nav className="sidebar-nav">
            {sidebarItems.map((item) => (
              <NavLink
                key={item.name}
                to={item.path}
                end={item.path === '/'}
                className={({ isActive }) =>
                  `sidebar-link ${isActive ? 'sidebar-link-active' : ''}`
                }
              >
                <span className="sidebar-link-icon">{item.icon}</span>
                <span>{item.name}</span>
              </NavLink>
            ))}
          </nav>
        </aside>

        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          {/* Topbar */}
          <header className="topbar">
            <div className="topbar-title">
              <span>Gold Rock Inn</span>
              <span className="topbar-admin">Admin</span>
            </div>
            <div className="topbar-actions">
              {/* Notifications */}
              <div className="topbar-icon relative" ref={notifRef} onClick={toggleNotifications} title="Notifications">
                <span>ðŸ””</span>
                {unreadCount > 0 && (
                  <span className="notif-badge">{unreadCount}</span>
                )}
                {notificationsOpen && (
                  <div className="dropdown-menu right">
                    <div className="dropdown-header">Recent Bookings</div>
                    {notifications.length === 0 ? (
                      <div className="dropdown-empty">No new bookings</div>
                    ) : (
                      <ul className="dropdown-list">
                        {notifications.map((n) => (
                          <li key={n.id} className="dropdown-item">
                            <div className="font-semibold">New booking</div>
                            <div className="text-xs text-gray-300">{n.user_name} booked {n.room_name}</div>
                            <div className="text-[10px] text-gray-400">{new Date(n.created_at).toLocaleString()}</div>
                          </li>
                        ))}
                      </ul>
                    )}
                    <div className="dropdown-footer">
                      <button className="link cta" onClick={(e) => { e.stopPropagation(); window.location.href = '/bookings'; }}>ðŸ“˜ View all bookings</button>
                    </div>
                  </div>
                )}
              </div>

              {/* Profile */}
              <div className="topbar-icon relative" ref={profileRef} onClick={toggleProfile} title="Profile">
                <span className="avatar">
                  <span className="avatar-initial">
                    {(adminUser?.full_name?.[0] || adminUser?.username?.[0] || 'A').toUpperCase()}
                  </span>
                </span>
                {profileOpen && (
                  <div className="dropdown-menu right">
                    <button className="dropdown-action highlight" onClick={(e) => { e.stopPropagation(); alert('Profile page coming soon'); setProfileOpen(false); }}>ðŸ‘¤ Profile</button>
                    <button className="dropdown-action danger" onClick={(e) => { e.stopPropagation(); handleLogout(); }}>â†ªï¸ Logout</button>
                  </div>
                )}
              </div>
            </div>
          </header>

          {/* Routed Content */}
          <Routes>
            <Route path="/login" element={<Navigate to="/" replace />} />
            <Route path="/" element={<Dashboard />} />
            <Route path="/rooms" element={<Rooms />} />
            <Route path="/guests" element={<Guests />} />
            <Route path="/customers" element={<Customers />} />
            <Route path="/front-desk" element={<FrontDesk />} />
            <Route path="/bookings" element={<Bookings />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
