import React, { useEffect, useMemo, useState } from 'react';
import { dashboardApi, supabase } from './lib/supabase';
import './Dashboard.css';

function Dashboard() {
  const [stats, setStats] = useState({
    todayCheckIns: 0,
    todayCheckOuts: 0,
    totalAvailableRooms: 0,
    totalOccupiedRooms: 0,
    checkInChange: '+0',
    checkOutChange: '+0',
    availableChange: '0',
    occupiedChange: '0',
  });
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Calendar state
  const [monthCursor, setMonthCursor] = useState(() => {
    const d = new Date();
    d.setDate(1);
    d.setHours(0,0,0,0);
    return d;
  });
  const [calendarLoading, setCalendarLoading] = useState(false);
  const [calendarError, setCalendarError] = useState(null);
  const [dayCounts, setDayCounts] = useState({}); // key: yyyy-mm-dd => count
  const [dayBookings, setDayBookings] = useState({}); // key: yyyy-mm-dd => bookings[]
  const [selectedDateKey, setSelectedDateKey] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const [statsData, roomsData] = await Promise.all([
          dashboardApi.getDashboardStats(),
          dashboardApi.getDashboardRooms(),
        ]);
        setStats(statsData);
        setRooms(roomsData);
        setError(null);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();

    // Refresh data every 30 seconds
    const interval = setInterval(fetchDashboardData, 30000);
    return () => clearInterval(interval);
  }, []);

  // Helpers for calendar
  const year = monthCursor.getFullYear();
  const month = monthCursor.getMonth();
  const startOfMonth = useMemo(() => new Date(year, month, 1), [year, month]);
  const endOfMonth = useMemo(() => new Date(year, month + 1, 0, 23, 59, 59, 999), [year, month]);
  const totalRooms = rooms?.length || 0;

  const formatKey = (d) => {
    const y = d.getFullYear();
    const m = String(d.getMonth() + 1).padStart(2, '0');
    const day = String(d.getDate()).padStart(2, '0');
    return `${y}-${m}-${day}`;
  };

  const buildMonthDays = () => {
    const firstWeekday = startOfMonth.getDay(); // 0=Sun
    const daysInMonth = endOfMonth.getDate();
    const days = [];
    // Pad leading blanks
    for (let i = 0; i < firstWeekday; i++) days.push(null);
    // Month days
    for (let d = 1; d <= daysInMonth; d++) {
      days.push(new Date(year, month, d));
    }
    return days;
  };

  const monthDays = useMemo(buildMonthDays, [startOfMonth, endOfMonth]);

  // Fetch reservations overlapping the month and compute counts
  useEffect(() => {
    let cancelled = false;
    (async () => {
      try {
        setCalendarLoading(true);
        setCalendarError(null);
        // Fetch reservations for a safe range (month +/- a few days) then filter on client
        const startISO = new Date(year, month, 1).toISOString();
        const endISO = new Date(year, month + 1, 0, 23, 59, 59, 999).toISOString();
        // Pull necessary fields
        const { data, error: err } = await supabase
          .from('reservations')
          .select('*')
          .order('created_at', { ascending: false })
          .limit(1000);
        if (err) throw err;

        const toISO = (r) => {
          const ci = r.checkin_at || r.check_in || r.start_date || r.created_at || null;
          const co = r.checkout_at || r.check_out || r.end_date || null;
          return { ci, co };
        };
        const overlaps = (ciISO, coISO, rangeStartISO, rangeEndISO) => {
          if (!ciISO && !coISO) return false;
          const ci = ciISO ? new Date(ciISO) : null;
          const co = coISO ? new Date(coISO) : null;
          const rs = new Date(rangeStartISO);
          const re = new Date(rangeEndISO);
          if (ci && !co) return ci <= re; // open ended
          if (!ci && co) return co >= rs;
          if (!ci && !co) return false;
          return ci <= re && co >= rs; // standard overlap
        };

        const monthBookings = (data || []).filter(r => {
          const { ci, co } = toISO(r);
          return overlaps(ci, co, startISO, endISO);
        });

        // Build per-day counts and booking buckets
        const counts = {};
        const buckets = {};
        const pushBooking = (key, booking) => {
          if (!buckets[key]) buckets[key] = [];
          buckets[key].push(booking);
        };
        const mapRow = (r) => {
          const { ci, co } = toISO(r);
          const ciD = ci ? new Date(ci) : null;
          const coD = co ? new Date(co) : null;
          const guest = r.user_name || r.name || r.customer_name || '';
          const room = r.room_name || r.room_no || r.room || '';
          const status = r.status || '';
          return { id: r.id, guest, room, status, ciISO: ci, coISO: co };
        };
        for (const r of monthBookings) {
          const { ciISO, coISO, guest, room, status, id } = mapRow(r);
          const start = ciISO ? new Date(ciISO) : startOfMonth;
          const end = coISO ? new Date(coISO) : endOfMonth;
          // Clamp to month
          const s = new Date(Math.max(start.getTime(), startOfMonth.getTime()));
          const e = new Date(Math.min(end.getTime(), endOfMonth.getTime()));
          for (let d = new Date(s); d <= e; d.setDate(d.getDate() + 1)) {
            const key = formatKey(d);
            counts[key] = (counts[key] || 0) + 1;
            pushBooking(key, { id, guest, room, status });
          }
        }

        if (!cancelled) {
          setDayCounts(counts);
          setDayBookings(buckets);
        }
      } catch (e) {
        if (!cancelled) setCalendarError(e.message || 'Failed to load calendar');
      } finally {
        if (!cancelled) setCalendarLoading(false);
      }
    })();
    return () => { cancelled = true; };
  }, [year, month]);

  if (loading) {
    return (
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        </div>
      </main>
    );
  }

  if (error) {
    return (
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
        </div>
      </main>
    );
  }

  return (
    <main className="dashboard-main">
      {/* Overview Cards */}
      <section className="mb-8">
        <div className="section-header">
          <h2 className="section-title">Overview</h2>
          <button onClick={() => window.location.reload()} className="btn-refresh">ðŸ”„ Refresh</button>
        </div>
        <div className="overview-grid">
          {[
            { title: "Today's Check-in", value: stats.todayCheckIns, change: `${stats.checkInChange} from yesterday`, icon: 'âž¡ï¸' },
            { title: "Today's Check-out", value: stats.todayCheckOuts, change: `${stats.checkOutChange} from yesterday`, icon: 'âž¡ï¸' },
            { title: 'Total Available Rooms', value: stats.totalAvailableRooms, change: `${stats.availableChange} since yesterday`, icon: 'ðŸ ' },
            { title: 'Total Occupied Rooms', value: stats.totalOccupiedRooms, change: `${stats.occupiedChange} since yesterday`, icon: 'ðŸ ' },
            { title: 'Total Revenue', value: `â‚±${(stats.totalRevenue || 0).toLocaleString()}`, change: 'from confirmed bookings', icon: 'ðŸ’°' },
          ].map((card, idx) => (
            <div key={idx} className="overview-card">
              <div className="card-row">
                <span className="card-title">{card.title}</span>
                <span className="card-icon">{card.icon}</span>
              </div>
              <div className="card-value">{card.value}</div>
              <div className="card-change">{card.change}</div>
            </div>
          ))}
        </div>
      </section>

      {/* Rooms Section */}
      <section className="mb-8">
        <div className="section-header">
          <h2 className="section-title">Rooms</h2>
          <button className="btn-outline" onClick={() => (window.location.href = '/rooms')}>View All Rooms</button>
        </div>
        <div className="rooms-grid">
          {/* Room Cards */}
          {rooms.length > 0 ? (
            rooms.map((room, idx) => (
              <div key={room.id || idx} className="room-card">
                <img
                  src={room.images && room.images.length > 0 ? room.images[0] : 'https://via.placeholder.com/400x200?text=No+Image'}
                  alt={room.name}
                  className="room-image"
                />
                <div className="room-body">
                  <div className="room-name">{room.name}</div>
                  <div className="room-occupied">{room.occupied} occupied</div>
                  <div className="room-price">â‚±{room.price.toLocaleString()}/night</div>
                  <div className="room-amenities">
                    {room.amenities && room.amenities.length > 0 ? (
                      room.amenities.map((amenity, i) => (
                        <span key={i} className="room-amenity">{amenity}</span>
                      ))
                    ) : (
                      <span className="room-amenity muted">No amenities listed</span>
                    )}
                  </div>
                  <div className="room-actions">
                    <button className="btn-view" onClick={() => (window.location.href = '/rooms')}>View Details</button>
                    <button className="btn-bookings" onClick={() => (window.location.href = '/bookings')}>View Bookings</button>
                  </div>
                </div>
              </div>
            ))
          ) : (
            <div className="col-span-full text-center py-8 text-gray-400">
              No rooms available.{' '}
              <button className="text-blue-400 hover:text-blue-300 underline" onClick={() => (window.location.href = '/rooms')}>
                Add your first room
              </button>
            </div>
          )}
        </div>
      </section>

      {/* Room Booking Overview Calendar */}
      <section className="mb-8">
        <div className="section-header">
          <h2 className="section-title">Room Booking Overview Calendar</h2>
          <div className="flex items-center gap-2">
            <button className="btn-outline" onClick={() => setMonthCursor(new Date(year, month - 1, 1))}>âŸµ</button>
            <div className="font-semibold">{monthCursor.toLocaleString(undefined, { month: 'long', year: 'numeric' })}</div>
            <button className="btn-outline" onClick={() => setMonthCursor(new Date(year, month + 1, 1))}>âŸ¶</button>
          </div>
        </div>
        {calendarError && <div className="bg-red-100 border border-red-400 text-red-700 px-3 py-2 rounded mb-2">{calendarError}</div>}
        <div className="rounded-lg p-4 bg-[#1f2a44]">
          <div className="grid grid-cols-7 gap-2 text-center text-gray-300 mb-2">
            <div>Sun</div><div>Mon</div><div>Tue</div><div>Wed</div><div>Thu</div><div>Fri</div><div>Sat</div>
          </div>
          <div className="grid grid-cols-7 gap-2">
            {monthDays.map((d, idx) => {
              if (!d) return <div key={`b-${idx}`} className="h-20 rounded bg-[#1b2438]" />;
              const key = formatKey(d);
              const booked = dayCounts[key] || 0;
              const cap = totalRooms || 1;
              const pct = Math.min(1, booked / cap);
              const color = pct <= 0.3 ? 'bg-green-600/80' : pct <= 0.7 ? 'bg-yellow-500/90' : 'bg-red-600/80';
              return (
                <button
                  key={key}
                  className="h-24 rounded bg-[#1b2438] hover:bg-[#212c45] transition p-2 text-left flex flex-col justify-between"
                  onClick={() => setSelectedDateKey(key)}
                >
                  <div className="text-sm text-gray-200 font-semibold">{d.getDate()}</div>
                  <div className={`text-xs text-white px-2 py-1 rounded self-start ${color}`}>{booked}/{cap} Booked</div>
                </button>
              );
            })}
          </div>
        </div>
      </section>

      {selectedDateKey && (
        <div className="fixed inset-0 bg-black/40 flex items-center justify-center z-50" onClick={() => setSelectedDateKey(null)}>
          <div className="bg-white text-black rounded-lg w-full max-w-xl p-6 relative" onClick={e => e.stopPropagation()}>
            <button className="absolute right-4 top-2 text-2xl" onClick={() => setSelectedDateKey(null)}>&times;</button>
            <h3 className="text-lg font-bold mb-2">Bookings on {selectedDateKey}</h3>
            {calendarLoading ? (
              <div className="text-sm text-gray-600">Loading...</div>
            ) : (
              <div className="space-y-2 max-h-[60vh] overflow-y-auto">
                {(dayBookings[selectedDateKey] || []).length === 0 ? (
                  <div className="text-sm text-gray-600">No bookings for this date.</div>
                ) : (
                  (dayBookings[selectedDateKey] || []).map((b) => (
                    <div key={b.id} className="border rounded px-3 py-2 flex items-center justify-between">
                      <div>
                        <div className="font-semibold text-sm">{b.guest || 'Guest'}</div>
                        <div className="text-xs text-gray-600">Room: {b.room || 'â€”'}</div>
                      </div>
                      <div className="text-xs px-2 py-1 rounded bg-[#eef]">{b.status || 'â€”'}</div>
                    </div>
                  ))
                )}
              </div>
            )}
          </div>
        </div>
      )}

      {/* Room Status */}
      <section>
        <h2 className="section-title mb-4">Room Status Overview</h2>
        <div className="status-panel">
          <div className="status-grid">
            <div className="status-item">
              <div className="status-available">{stats.totalAvailableRooms}</div>
              <div className="status-label">Available Rooms</div>
            </div>
            <div className="status-item">
              <div className="status-occupied">{stats.totalOccupiedRooms}</div>
              <div className="status-label">Occupied Rooms</div>
            </div>
            <div className="status-item">
              <div className="status-maint">{rooms.filter((room) => room.status === 'maintenance').length}</div>
              <div className="status-label">Under Maintenance</div>
            </div>
          </div>
          <div className="status-updated">Last updated: {new Date().toLocaleTimeString()}</div>
        </div>
      </section>
    </main>
  );
}

export default Dashboard;
