import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import Rooms from './Rooms.jsx';
import Guests from './Guests.jsx';
import FrontDesk from './FrontDesk';
import Bookings from './Bookings';
import { dashboardApi, supabase } from './lib/supabase';
import './Navbar.css';

const sidebarItems = [
  { name: 'Dashboard', icon: 'ðŸ ', path: '/' },
  { name: 'Rooms', icon: 'ðŸ›ï¸', path: '/rooms' },
  { name: 'Guests', icon: 'ðŸ‘¤', path: '/guests' },
  { name: 'Front Desk', icon: 'ðŸ›Žï¸', path: '/front-desk' },
  { name: 'Bookings', icon: 'ðŸ“–', path: '/bookings' },
];

function Dashboard() {
  const [stats, setStats] = useState({
    todayCheckIns: 0,
    todayCheckOuts: 0,
    totalAvailableRooms: 0,
    totalOccupiedRooms: 0,
    checkInChange: '+0',
    checkOutChange: '+0',
    availableChange: '0',
    occupiedChange: '0'
  });
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const [statsData, roomsData] = await Promise.all([
          dashboardApi.getDashboardStats(),
          dashboardApi.getDashboardRooms()
        ]);
        setStats(statsData);
        setRooms(roomsData);
        setError(null);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();

    // Refresh data every 30 seconds
    const interval = setInterval(fetchDashboardData, 30000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        </div>
      </main>
    );
  }

  if (error) {
    return (
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
        </div>
      </main>
    );
  }

  return (
    <main className="flex-1 p-8 overflow-y-auto">
      {/* Overview Cards */}
      <section className="mb-8">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-xl font-semibold">Overview</h2>
          <button 
            onClick={() => window.location.reload()}
            className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm"
          >
            ðŸ”„ Refresh
          </button>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-5 gap-4">
          {[
            { title: "Today's Check-in", value: stats.todayCheckIns, change: `${stats.checkInChange} from yesterday`, icon: 'âž¡ï¸' },
            { title: "Today's Check-out", value: stats.todayCheckOuts, change: `${stats.checkOutChange} from yesterday`, icon: 'âž¡ï¸' },
            { title: 'Total Available Rooms', value: stats.totalAvailableRooms, change: `${stats.availableChange} since yesterday`, icon: 'ðŸš—' },
            { title: 'Total Occupied Rooms', value: stats.totalOccupiedRooms, change: `${stats.occupiedChange} since yesterday`, icon: 'ðŸ ' },
            { title: 'Total Revenue', value: `â‚±${(stats.totalRevenue || 0).toLocaleString()}`, change: 'from confirmed bookings', icon: 'ðŸ’°' },
          ].map((card, idx) => (
            <div key={idx} className="bg-[#232f47] rounded-lg p-5 flex flex-col gap-2 shadow">
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-300">{card.title}</span>
                <span className="text-lg">{card.icon}</span>
              </div>
              <div className="text-3xl font-bold">{card.value}</div>
              <div className="text-xs text-green-400">{card.change}</div>
            </div>
          ))}
        </div>
      </section>
      {/* Rooms Section */}
      <section className="mb-8">
        <div className="flex items-center justify-between mb-4">
          <h2 className="text-xl font-semibold">Rooms</h2>
          <button 
            className="bg-[#232f47] px-4 py-2 rounded text-sm font-semibold hover:bg-[#2d3a56] transition"
            onClick={() => window.location.href = '/rooms'}
          >
            View All Rooms
          </button>
        </div>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          {/* Room Cards */}
          {rooms.length > 0 ? rooms.map((room, idx) => (
            <div key={room.id || idx} className="bg-[#232f47] rounded-lg overflow-hidden shadow flex flex-col">
              <img 
                src={room.images && room.images.length > 0 ? room.images[0] : 'https://via.placeholder.com/400x200?text=No+Image'} 
                alt={room.name} 
                className="h-32 w-full object-cover" 
              />
              <div className="p-4 flex-1 flex flex-col">
                <div className="font-semibold text-lg mb-1">{room.name}</div>
                <div className="text-xs text-gray-400 mb-2">{room.occupied} occupied</div>
                <div className="font-bold text-base mb-2">â‚±{room.price.toLocaleString()}/night</div>
                <div className="flex flex-wrap gap-2 mb-2">
                  {room.amenities && room.amenities.length > 0 ? room.amenities.map((amenity, i) => (
                    <span key={i} className="bg-[#1a253a] text-xs px-2 py-1 rounded text-green-300">{amenity}</span>
                  )) : (
                    <span className="bg-[#1a253a] text-xs px-2 py-1 rounded text-gray-400">No amenities listed</span>
                  )}
                </div>
                <div className="flex gap-2 mt-auto">
                  <button 
                    className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-xs"
                    onClick={() => window.location.href = '/rooms'}
                  >
                    View Details
                  </button>
                  <button 
                    className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-xs"
                    onClick={() => window.location.href = '/bookings'}
                  >
                    View Bookings
                  </button>
                </div>
              </div>
            </div>
          )) : (
            <div className="col-span-full text-center py-8 text-gray-400">
              No rooms available. <button 
                className="text-blue-400 hover:text-blue-300 underline"
                onClick={() => window.location.href = '/rooms'}
              >
                Add your first room
              </button>
            </div>
          )}
        </div>
      </section>
      {/* Room Status */}
      <section>
        <h2 className="text-xl font-semibold mb-4">Room Status Overview</h2>
        <div className="bg-[#232f47] rounded-lg p-6">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="text-center">
              <div className="text-3xl font-bold text-green-400">{stats.totalAvailableRooms}</div>
              <div className="text-sm text-gray-300">Available Rooms</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-blue-400">{stats.totalOccupiedRooms}</div>
              <div className="text-sm text-gray-300">Occupied Rooms</div>
            </div>
            <div className="text-center">
              <div className="text-3xl font-bold text-yellow-400">
                {rooms.filter(room => room.status === 'maintenance').length}
              </div>
              <div className="text-sm text-gray-300">Under Maintenance</div>
            </div>
          </div>
          <div className="mt-6 pt-6 border-t border-gray-600">
            <div className="text-sm text-gray-400 text-center">
              Last updated: {new Date().toLocaleTimeString()}
            </div>
          </div>
        </div>
      </section>
    </main>
  );
}

function App() {
  const [notificationsOpen, setNotificationsOpen] = useState(false);
  const [profileOpen, setProfileOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const notifRef = useRef(null);
  const profileRef = useRef(null);

  useEffect(() => {
    // Subscribe to new reservations for notifications
    const channel = supabase
      .channel('reservations-notify')
      .on('postgres_changes', { event: 'INSERT', schema: 'public', table: 'reservations' }, (payload) => {
        const b = payload.new || {};
        const item = {
          id: b.id,
          user_name: b.user_name,
          room_name: b.room_name,
          created_at: b.created_at,
          status: b.status,
        };
        setNotifications((prev) => [item, ...prev].slice(0, 10));
        setUnreadCount((c) => c + 1);
      })
      .subscribe();

    return () => {
      try { supabase.removeChannel(channel); } catch (e) { /* noop */ }
    };
  }, []);

  useEffect(() => {
    // Close dropdowns on outside click
    const onClick = (e) => {
      if (notifRef.current && !notifRef.current.contains(e.target)) {
        setNotificationsOpen(false);
      }
      if (profileRef.current && !profileRef.current.contains(e.target)) {
        setProfileOpen(false);
      }
    };
    document.addEventListener('click', onClick);
    return () => document.removeEventListener('click', onClick);
  }, []);

  const toggleNotifications = () => {
    setNotificationsOpen((o) => !o);
    setUnreadCount(0);
    setProfileOpen(false);
  };

  const toggleProfile = () => {
    setProfileOpen((o) => !o);
    setNotificationsOpen(false);
  };

  const handleLogout = async () => {
    try {
      await supabase.auth.signOut();
      window.location.href = '/';
    } catch (e) {
      alert('Failed to logout');
    }
  };

  return (
    <Router>
      <div className="flex min-h-screen bg-[#151e2e] text-white">
        {/* Sidebar */}
        <aside className="sidebar">
          <div className="sidebar-logo">
            <div className="sidebar-logo-icon">M</div>
            <div>
              <div className="sidebar-logo-title">GOLD ROCK</div>
              <div className="sidebar-logo-sub">INN</div>
            </div>
          </div>
          <nav className="sidebar-nav">
            {sidebarItems.map((item) => (
              <NavLink
                key={item.name}
                to={item.path}
                end={item.path === '/'}
                className={({ isActive }) =>
                  `sidebar-link ${isActive ? 'sidebar-link-active' : ''}`
                }
              >
                <span className="sidebar-link-icon">{item.icon}</span>
                <span>{item.name}</span>
              </NavLink>
            ))}
          </nav>
        </aside>

        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          {/* Topbar */}
          <header className="topbar">
            <div className="topbar-title">
              <span>Gold Rock Inn</span>
              <span className="topbar-admin">Admin</span>
            </div>
            <div className="topbar-actions">
              <input type="text" placeholder="Search..." className="topbar-search" />
              {/* Notifications */}
              <div className="topbar-icon relative" ref={notifRef} onClick={toggleNotifications} title="Notifications">
                <span>ðŸ””</span>
                {unreadCount > 0 && (
                  <span className="badge">{unreadCount}</span>
                )}
                {notificationsOpen && (
                  <div className="dropdown-menu right">
                    <div className="dropdown-header">Recent Bookings</div>
                    {notifications.length === 0 ? (
                      <div className="dropdown-empty">No new bookings</div>
                    ) : (
                      <ul className="dropdown-list">
                        {notifications.map((n) => (
                          <li key={n.id} className="dropdown-item">
                            <div className="font-semibold">New booking</div>
                            <div className="text-xs text-gray-300">{n.user_name} booked {n.room_name}</div>
                            <div className="text-[10px] text-gray-400">{new Date(n.created_at).toLocaleString()}</div>
                          </li>
                        ))}
                      </ul>
                    )}
                    <div className="dropdown-footer">
                      <button className="link" onClick={(e) => { e.stopPropagation(); window.location.href = '/bookings'; }}>View all bookings</button>
                    </div>
                  </div>
                )}
              </div>

              {/* Profile */}
              <div className="topbar-icon relative" ref={profileRef} onClick={toggleProfile} title="Profile">
                <span>ðŸ‘¤</span>
                {profileOpen && (
                  <div className="dropdown-menu right">
                    <button className="dropdown-action" onClick={(e) => { e.stopPropagation(); alert('Profile page coming soon'); setProfileOpen(false); }}>Profile</button>
                    <button className="dropdown-action" onClick={(e) => { e.stopPropagation(); handleLogout(); }}>Logout</button>
                  </div>
                )}
              </div>
            </div>
          </header>

          {/* Routed Content */}
          <Routes>
            <Route path="/" element={<Dashboard />} />
            <Route path="/rooms" element={<Rooms />} />
            <Route path="/guests" element={<Guests />} />
            <Route path="/front-desk" element={<FrontDesk />} />
            {/* <Route path="/rate" element={<Rate />} /> */}
            <Route path="/bookings" element={<Bookings />} />
            {/* <Route path="/settings" element={<Settings />} /> */}
            {/* <Route path="/help" element={<Help />} /> */}
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
