import React, { useState } from 'react';

const initialRates = [
  { roomType: 'Family Suite', rate: 3300 },
  { roomType: 'Junior Suite', rate: 2200 },
  { roomType: 'Twin Superior', rate: 1600 },
  { roomType: 'Double Superior', rate: 1500 },
  { roomType: 'Studio Room', rate: 1400 },
];

function Rate() {
  const [rates, setRates] = useState(initialRates);
  const [editIdx, setEditIdx] = useState(null);
  const [editValue, setEditValue] = useState('');

  const handleEdit = (idx) => {
    setEditIdx(idx);
    setEditValue(rates[idx].rate);
  };

  const handleSave = (idx) => {
    const updated = [...rates];
    updated[idx].rate = Number(editValue);
    setRates(updated);
    setEditIdx(null);
    setEditValue('');
  };

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold mb-6">Room Rate Management</h2>
      <div className="max-w-xl mx-auto bg-[#232f47] rounded-lg shadow-lg p-6">
        <table className="w-full text-left text-white">
          <thead>
            <tr>
              <th className="py-2 px-4">Room Type</th>
              <th className="py-2 px-4">Rate (₱/night)</th>
              <th className="py-2 px-4">Actions</th>
            </tr>
          </thead>
          <tbody>
            {rates.map((row, idx) => (
              <tr key={row.roomType} className="border-t border-[#1a253a]">
                <td className="py-2 px-4 font-semibold">{row.roomType}</td>
                <td className="py-2 px-4">
                  {editIdx === idx ? (
                    <input
                      type="number"
                      className="bg-[#151e2e] text-white rounded px-2 py-1 w-32"
                      value={editValue}
                      onChange={e => setEditValue(e.target.value)}
                    />
                  ) : (
                    `₱${row.rate.toLocaleString()}`
                  )}
                </td>
                <td className="py-2 px-4">
                  {editIdx === idx ? (
                    <>
                      <button className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded mr-2" onClick={() => handleSave(idx)}>Save</button>
                      <button className="bg-gray-400 px-3 py-1 rounded" onClick={() => setEditIdx(null)}>Cancel</button>
                    </>
                  ) : (
                    <button className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded" onClick={() => handleEdit(idx)}>Edit</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default Rate;
