import React, { useState, useMemo } from 'react';

const initialEmployees = ['Alice', 'Bob', 'Carol', 'Dave', 'Eve'];

function getDaysInMonth(year, month) {
  return new Date(year, month + 1, 0).getDate();
}

function getMonthMatrix(year, month) {
  const daysInMonth = getDaysInMonth(year, month);
  const firstDay = new Date(year, month, 1).getDay();
  const matrix = [];
  let week = Array(firstDay).fill(null);
  for (let day = 1; day <= daysInMonth; day++) {
    week.push(day);
    if (week.length === 7) {
      matrix.push(week);
      week = [];
    }
  }
  if (week.length) {
    while (week.length < 7) week.push(null);
    matrix.push(week);
  }
  return matrix;
}

const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

// Deterministic color per employee for aesthetic badges
function badgeColor(name) {
  const map = {
    Alice: 'bg-blue-600/90 text-white',
    Bob: 'bg-red-500/90 text-white',
    Carol: 'bg-green-600/90 text-white',
    Dave: 'bg-indigo-600/90 text-white',
    Eve: 'bg-purple-600/90 text-white'
  };
  return map[name] || 'bg-slate-600/90 text-white';
}

function FrontDesk() {
  const today = new Date();
  const [year, setYear] = useState(today.getFullYear());
  const [month, setMonth] = useState(today.getMonth());
  const [employees, setEmployees] = useState(initialEmployees);
  // Single assigned employee per day (client-side only)
  const [assignments, setAssignments] = useState(() => {
    let empIdx = 0;
    const a = [];
    for (let day = 1; day <= getDaysInMonth(year, month); day++) {
      a[day] = employees[empIdx % employees.length];
      empIdx++;
    }
    return a;
  });
  const [showAddEmp, setShowAddEmp] = useState(false);
  const [newEmp, setNewEmp] = useState('');
  const [showEdit, setShowEdit] = useState(null); // day number
  const [editEmp, setEditEmp] = useState('');
  const [showDeleteEmp, setShowDeleteEmp] = useState(null);

  // Drawer state (no DB)
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [selectedDay, setSelectedDay] = useState(null);
  const [pickerEmp, setPickerEmp] = useState(initialEmployees[0]);

  // Update assignments when month/year/employees change
  React.useEffect(() => {
    let empIdx = 0;
    const a = [];
    for (let day = 1; day <= getDaysInMonth(year, month); day++) {
      a[day] = employees[empIdx % employees.length];
      empIdx++;
    }
    setAssignments(a);
    // eslint-disable-next-line
  }, [year, month, employees.length]);

  const daysMatrix = useMemo(() => getMonthMatrix(year, month), [year, month]);
  const monthName = useMemo(() => new Date(year, month).toLocaleString('default', { month: 'long' }), [year, month]);

  const handlePrevMonth = () => {
    if (month === 0) { setMonth(11); setYear(y => y - 1); } else { setMonth(m => m - 1); }
  };
  const handleNextMonth = () => {
    if (month === 11) { setMonth(0); setYear(y => y + 1); } else { setMonth(m => m + 1); }
  };

  const handleAddEmp = () => {
    if (newEmp.trim() && !employees.includes(newEmp.trim())) {
      setEmployees([...employees, newEmp.trim()]);
      setNewEmp('');
      setShowAddEmp(false);
    }
  };

  const handleEditAssignment = (day) => {
    if (editEmp && employees.includes(editEmp)) {
      setAssignments(a => {
        const copy = [...a];
        copy[day] = editEmp;
        return copy;
      });
      setShowEdit(null);
      setEditEmp('');
    }
  };

  const handleDeleteEmp = (emp) => {
    setEmployees(emps => emps.filter(e => e !== emp));
    setAssignments(a => a.map(name => (name === emp ? employees[0] : name)));
    setShowDeleteEmp(null);
  };

  // Drawer helpers
  const openDrawer = (day) => { setSelectedDay(day); setDrawerOpen(true); };
  const closeDrawer = () => setDrawerOpen(false);
  const assignEmployeeToSelected = () => {
    if (!selectedDay) return;
    setAssignments(a => {
      const copy = [...a];
      copy[selectedDay] = pickerEmp;
      return copy;
    });
  };
  const clearAssignmentsForSelected = () => {
    if (!selectedDay) return;
    setAssignments(a => {
      const copy = [...a];
      copy[selectedDay] = '';
      return copy;
    });
  };

  return (
    <div className="p-8">
      <div className="max-w-6xl mx-auto">
        <div className="mb-6">
          <h2 className="text-3xl font-bold text-white">Front Desk Duty Calendar</h2>
          <p className="text-slate-300 mt-1">Assign and manage front desk duties per day.</p>
        </div>

        <div className="bg-[#1e2a44] rounded-2xl shadow-xl p-6 border border-white/5">
          <div className="flex justify-between items-center mb-5">
            <button className="inline-flex items-center gap-2 bg-slate-600/70 hover:bg-slate-600 text-white px-3 py-1.5 rounded-lg shadow-sm" onClick={handlePrevMonth}>
              <span className="text-lg"></span>
            </button>
            <span className="text-xl font-semibold text-white">{monthName} {year}</span>
            <button className="inline-flex items-center gap-2 bg-slate-600/70 hover:bg-slate-600 text-white px-3 py-1.5 rounded-lg shadow-sm" onClick={handleNextMonth}>
              <span className="text-lg"></span>
            </button>
          </div>

          <div className="grid grid-cols-7 gap-2 text-center mb-2">
            {weekDays.map(d => (
              <div key={d} className="font-semibold text-blue-300">{d}</div>
            ))}
          </div>

          <div className="grid grid-cols-7 gap-2">
            {daysMatrix.flat().map((day, idx) => (
              <div
                key={idx}
                className={`h-24 rounded-xl border border-white/10 flex flex-col items-start justify-start p-2 text-left transition ${day ? 'bg-[#162136] text-white hover:bg-[#1a2741] cursor-pointer' : 'opacity-0'}`}
                onClick={() => day && openDrawer(day)}
              >
                {day && (
                  <>
                    <div className="text-sm font-bold">{day}</div>
                    <div className="mt-2 flex flex-wrap gap-1">
                      {assignments[day] ? (
                        <span className={`text-[11px] px-2 py-0.5 rounded-full ${badgeColor(assignments[day])} shadow-sm`}>{assignments[day]}</span>
                      ) : (
                        <span className="text-[10px] px-2 py-0.5 rounded-full bg-slate-600/50 text-white">Unassigned</span>
                      )}
                      <button
                        className="ml-1 text-xs bg-yellow-400 text-black px-1.5 rounded hover:bg-yellow-300"
                        onClick={(e) => { e.stopPropagation(); setShowEdit(day); setEditEmp(assignments[day]); }}
                        title="Edit"
                      ></button>
                    </div>
                  </>
                )}
              </div>
            ))}
          </div>

          <div className="mt-6">
            <h3 className="font-semibold mb-2 text-blue-300">Employee Legend</h3>
            <div className="flex gap-2 flex-wrap items-center">
              {employees.map(e => (
                <span key={e} className={`px-3 py-1 rounded-full text-sm shadow-sm ${badgeColor(e)} flex items-center gap-2`}>
                  {e}
                  <button className="bg-white/20 hover:bg-white/30 text-white px-1 rounded text-xs" onClick={() => setShowDeleteEmp(e)} title="Delete"></button>
                </span>
              ))}
              <button className="bg-green-600 hover:bg-green-700 text-white px-3 py-1.5 rounded-lg text-sm shadow-sm" onClick={() => setShowAddEmp(true)}>+ Add Employee</button>
            </div>
          </div>
        </div>
      </div>

      {/* Right-side Drawer */}
      {drawerOpen && (
        <div className="fixed inset-0 z-50">
          <div className="absolute inset-0 bg-black/50 backdrop-blur-sm" onClick={closeDrawer} />
          <div className="absolute right-0 top-0 h-full w-full sm:w-[420px] bg-white text-gray-900 shadow-2xl p-6 overflow-y-auto">
            <div className="flex items-center justify-between mb-4">
              <div>
                <div className="text-xs uppercase tracking-wide text-gray-500">Selected Day</div>
                <div className="text-lg font-semibold"> {monthName} {selectedDay}, {year}</div>
              </div>
              <button className="text-2xl leading-none" onClick={closeDrawer}>&times;</button>
            </div>

            <div className="mb-6">
              <div className="font-semibold mb-2"> Assigned Employees</div>
              <div className="rounded-xl border border-gray-200 overflow-hidden">
                {!assignments[selectedDay] && (
                  <div className="p-4 text-sm text-gray-500">No one assigned yet.</div>
                )}
                {assignments[selectedDay] && (
                  <div className="flex items-center justify-between p-4">
                    <div>
                      <div className="font-medium">{assignments[selectedDay]}</div>
                      <div className="text-xs text-gray-500">Assigned</div>
                    </div>
                    <button className="text-red-600 hover:text-red-700 text-sm" onClick={clearAssignmentsForSelected} title="Remove">Remove</button>
                  </div>
                )}
              </div>

              <div className="mt-4 flex gap-2">
                <select className="border rounded-lg px-2 py-2 text-sm flex-1" value={pickerEmp} onChange={e => setPickerEmp(e.target.value)}>
                  {employees.map(e => <option key={e} value={e}>{e}</option>)}
                </select>
                <button className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-2 rounded-lg text-sm shadow-sm" onClick={assignEmployeeToSelected}>+ Add Employee</button>
              </div>

              <div className="mt-3">
                <button className="text-sm text-red-600 hover:text-red-700" onClick={clearAssignmentsForSelected}> Remove All</button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Add Employee Modal */}
      {showAddEmp && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setShowAddEmp(false)}>
          <div className="bg-white text-black rounded-2xl p-8 w-full max-w-md relative shadow-xl" onClick={e => e.stopPropagation()}>
            <button className="absolute top-3 right-5 text-2xl" onClick={() => setShowAddEmp(false)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Add Employee</h2>
            <input
              className="border p-3 rounded-lg w-full mb-4"
              value={newEmp}
              onChange={e => setNewEmp(e.target.value)}
              placeholder="Employee Name"
            />
            <div className="flex justify-end gap-2">
              <button className="bg-gray-200 px-4 py-2 rounded-lg" onClick={() => setShowAddEmp(false)}>Cancel</button>
              <button className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg shadow" onClick={handleAddEmp}>Add</button>
            </div>
          </div>
        </div>
      )}

      {/* Edit Assignment Modal */}
      {showEdit && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setShowEdit(null)}>
          <div className="bg-white text-black rounded-2xl p-8 w-full max-w-md relative shadow-xl" onClick={e => e.stopPropagation()}>
            <button className="absolute top-3 right-5 text-2xl" onClick={() => setShowEdit(null)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Edit Assignment for Day {showEdit}</h2>
            <select
              className="border p-3 rounded-lg w-full mb-4"
              value={editEmp}
              onChange={e => setEditEmp(e.target.value)}
            >
              {employees.map(e => (
                <option key={e} value={e}>{e}</option>
              ))}
            </select>
            <div className="flex justify-end gap-2">
              <button className="bg-gray-200 px-4 py-2 rounded-lg" onClick={() => setShowEdit(null)}>Cancel</button>
              <button className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg shadow" onClick={() => handleEditAssignment(showEdit)}>Save</button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Employee Modal */}
      {showDeleteEmp && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setShowDeleteEmp(null)}>
          <div className="bg-white text-black rounded-2xl p-8 w-full max-w-md relative shadow-xl" onClick={e => e.stopPropagation()}>
            <button className="absolute top-3 right-5 text-2xl" onClick={() => setShowDeleteEmp(null)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Delete Employee</h2>
            <p>Are you sure you want to delete <b>{showDeleteEmp}</b>?</p>
            <div className="flex justify-end gap-2 mt-4">
              <button className="bg-gray-200 px-4 py-2 rounded-lg" onClick={() => setShowDeleteEmp(null)}>Cancel</button>
              <button className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg shadow" onClick={() => handleDeleteEmp(showDeleteEmp)}>Delete</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default FrontDesk;
