import React, { useState } from 'react';

const initialRoomData = [
  {
    id: 1,
    name: "Family Suite",
    beds: "3 Double beds",
    capacity: "6 Capacity",
    features: [
      "Orthopedic Bed",
      "Split-Type Inverter Aircon",
      "Private Restroom with Hot and Cold Shower",
      "Coffee set-up",
      "Free Wi-Fi Access"
    ],
    images: [
      "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80"
    ],
    price: 3300,
    available: 3,
    occupied: 2,
    reserved: 1,
    maintenance: false,
    bookings: [
      { guest: "John Doe", date: "2024-07-01" },
      { guest: "Jane Smith", date: "2024-06-28" }
    ]
  },
  {
    id: 2,
    name: "Junior Suite",
    beds: "1 Queen & 1 Single Bed",
    capacity: "3 Capacity",
    features: [
      "Orthopedic Bed",
      "Split-Type Inverter Aircon",
      "Private Restroom with Hot and Cold Shower",
      "Coffee set-up",
      "Free Wi-Fi Access"
    ],
    images: [
      "https://images.unsplash.com/photo-1512918728675-ed5a9ecdebfd?auto=format&fit=crop&w=400&q=80"
    ],
    price: 2200,
    available: 2,
    occupied: 1,
    reserved: 0,
    maintenance: false,
    bookings: []
  },
  {
    id: 3,
    name: "Twin Superior",
    beds: "2 Single Beds",
    capacity: "2 Capacity",
    features: [
      "Orthopedic Bed",
      "Split-Type Inverter Aircon",
      "Private Restroom with Hot and Cold Shower",
      "Coffee set-up",
      "Free Wi-Fi Access"
    ],
    images: [
      "https://images.unsplash.com/photo-1507089947368-19c1da9775ae?auto=format&fit=crop&w=400&q=80"
    ],
    price: 1600,
    available: 4,
    occupied: 0,
    reserved: 0,
    maintenance: false,
    bookings: []
  },
  {
    id: 4,
    name: "Double Superior",
    beds: "1 Double Bed",
    capacity: "2 Capacity",
    features: [
      "Orthopedic Bed",
      "Split-Type Inverter Aircon",
      "Private Restroom with Hot and Cold Shower",
      "Coffee set-up",
      "Free Wi-Fi Access"
    ],
    images: [
      "https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80"
    ],
    price: 1500,
    available: 2,
    occupied: 2,
    reserved: 0,
    maintenance: true,
    bookings: []
  },
  {
    id: 5,
    name: "Studio Room",
    beds: "1 Double Bed",
    capacity: "2 Capacity",
    features: [
      "Orthopedic Bed",
      "Split-Type Inverter Aircon",
      "Private Restroom with Hot and Cold Shower",
      "Coffee set-up",
      "Free Wi-Fi Access"
    ],
    images: [
      "https://images.unsplash.com/photo-1519974719765-e6559eac2575?auto=format&fit=crop&w=400&q=80"
    ],
    price: 1400,
    available: 5,
    occupied: 0,
    reserved: 0,
    maintenance: false,
    bookings: []
  }
];

const amenitiesList = [
  "Orthopedic Bed",
  "Split-Type Inverter Aircon",
  "Private Restroom with Hot and Cold Shower",
  "Coffee set-up",
  "Free Wi-Fi Access",
  "Mini Bar",
  "Smart TV",
  "Sofa"
];

function Rooms() {
  const [rooms, setRooms] = useState(initialRoomData);
  const [selected, setSelected] = useState([]); // for bulk actions
  const [search, setSearch] = useState("");
  const [filter, setFilter] = useState({ capacity: "", status: "" });
  const [sort, setSort] = useState("");
  const [showAddEdit, setShowAddEdit] = useState(false);
  const [editRoom, setEditRoom] = useState(null);
  const [showDelete, setShowDelete] = useState(null);
  const [showBookings, setShowBookings] = useState(null);
  const [showGallery, setShowGallery] = useState(null);
  const [importModal, setImportModal] = useState(false);

  // Filter, search, sort logic (simplified)
  let displayedRooms = rooms.filter(r =>
    r.name.toLowerCase().includes(search.toLowerCase()) &&
    (!filter.capacity || r.capacity === filter.capacity) &&
    (!filter.status || (filter.status === "maintenance" ? r.maintenance : !r.maintenance))
  );
  if (sort === "price") displayedRooms = [...displayedRooms].sort((a, b) => a.price - b.price);
  if (sort === "capacity") displayedRooms = [...displayedRooms].sort((a, b) => a.capacity.localeCompare(b.capacity));

  // Bulk actions
  const handleBulkDelete = () => {
    setRooms(rooms.filter(r => !selected.includes(r.id)));
    setSelected([]);
  };

  // Add/Edit Room
  const handleSaveRoom = (room) => {
    if (editRoom) {
      setRooms(rooms.map(r => r.id === room.id ? room : r));
    } else {
      setRooms([...rooms, { ...room, id: Date.now() }]);
    }
    setShowAddEdit(false);
    setEditRoom(null);
  };

  // Export (CSV)
  const handleExport = () => {
    const csv = [
      ["Name", "Beds", "Capacity", "Price", "Available", "Occupied", "Reserved", "Maintenance"],
      ...rooms.map(r => [r.name, r.beds, r.capacity, r.price, r.available, r.occupied, r.reserved, r.maintenance ? "Yes" : "No"])
    ].map(row => row.join(",")).join("\n");
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'rooms.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="p-8">
      {/* Top bar: Add, Export, Import, Search, Filter, Sort, Bulk Actions */}
      <div className="flex flex-wrap gap-2 mb-6 items-center">
        <button className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded" onClick={() => { setShowAddEdit(true); setEditRoom(null); }}>Add Room</button>
        <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded" onClick={handleExport}>Export CSV</button>
        <button className="bg-yellow-600 hover:bg-yellow-700 text-white px-4 py-2 rounded" onClick={() => setImportModal(true)}>Import</button>
        <input type="text" placeholder="Search rooms..." className="px-3 py-2 rounded bg-[#232f47] text-white" value={search} onChange={e => setSearch(e.target.value)} />
        <select className="px-2 py-2 rounded bg-[#232f47] text-white" value={filter.capacity} onChange={e => setFilter(f => ({ ...f, capacity: e.target.value }))}>
          <option value="">All Capacities</option>
          {[...new Set(rooms.map(r => r.capacity))].map(c => <option key={c} value={c}>{c}</option>)}
        </select>
        <select className="px-2 py-2 rounded bg-[#232f47] text-white" value={filter.status} onChange={e => setFilter(f => ({ ...f, status: e.target.value }))}>
          <option value="">All Statuses</option>
          <option value="maintenance">Under Maintenance</option>
          <option value="active">Active</option>
        </select>
        <select className="px-2 py-2 rounded bg-[#232f47] text-white" value={sort} onChange={e => setSort(e.target.value)}>
          <option value="">Sort By</option>
          <option value="price">Price</option>
          <option value="capacity">Capacity</option>
        </select>
        {selected.length > 0 && (
          <>
            <span className="ml-4">Bulk Actions:</span>
            <button className="bg-red-600 hover:bg-red-700 text-white px-3 py-2 rounded" onClick={handleBulkDelete}>Delete Selected</button>
          </>
        )}
      </div>

      {/* Room Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {displayedRooms.map((room) => (
          <div key={room.id} className={`bg-[#232f47] rounded-lg shadow-lg overflow-hidden flex flex-col relative ${room.maintenance ? 'opacity-60' : ''}`}>
            <input type="checkbox" className="absolute top-3 left-3 scale-125" checked={selected.includes(room.id)} onChange={e => setSelected(sel => e.target.checked ? [...sel, room.id] : sel.filter(id => id !== room.id))} />
            <img src={room.images[0]} alt={room.name} className="h-48 w-full object-cover" onClick={() => setShowGallery(room)} />
            <div className="p-5 flex-1 flex flex-col">
              <div className="font-bold text-xl mb-1 flex items-center gap-2">
                {room.name}
                {room.maintenance && <span className="bg-yellow-500 text-xs px-2 py-1 rounded">Maintenance</span>}
              </div>
              <div className="text-sm text-gray-300 mb-2">{room.beds} • {room.capacity}</div>
              <div className="mb-2">
                <span className="font-semibold text-green-300">₱{room.price.toLocaleString()}</span>
                <span className="text-xs text-gray-400 ml-1">/ night</span>
              </div>
              <div className="mb-2 flex gap-2 text-xs">
                <span className="bg-blue-700 px-2 py-1 rounded">Available: {room.available}</span>
                <span className="bg-green-700 px-2 py-1 rounded">Occupied: {room.occupied}</span>
                <span className="bg-purple-700 px-2 py-1 rounded">Reserved: {room.reserved}</span>
              </div>
              <div className="mb-2">
                <div className="font-semibold text-sm mb-1">Features:</div>
                <ul className="list-disc list-inside text-xs text-gray-200">
                  {room.features.map(f => <li key={f}>{f}</li>)}
                </ul>
              </div>
              <div className="mt-auto flex gap-2 flex-wrap">
                <button className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-xs" onClick={() => { setShowAddEdit(true); setEditRoom(room); }}>Edit</button>
                <button className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded text-xs" onClick={() => setShowDelete(room)}>Delete</button>
                <button className="bg-gray-600 hover:bg-gray-700 text-white px-3 py-1 rounded text-xs" onClick={() => setShowBookings(room)}>View Bookings</button>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Add/Edit Room Modal */}
      {showAddEdit && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => { setShowAddEdit(false); setEditRoom(null); }}>
          <div className="bg-white text-black rounded-lg p-8 w-full max-w-lg relative" onClick={e => e.stopPropagation()}>
            <button className="absolute top-2 right-4 text-2xl" onClick={() => { setShowAddEdit(false); setEditRoom(null); }}>&times;</button>
            <h2 className="text-xl font-bold mb-4">{editRoom ? 'Edit Room' : 'Add Room'}</h2>
            {/* Form fields (simplified) */}
            <form onSubmit={e => { e.preventDefault();
              const form = e.target;
              const newRoom = {
                id: editRoom ? editRoom.id : Date.now(),
                name: form.name.value,
                beds: form.beds.value,
                capacity: form.capacity.value,
                price: Number(form.price.value),
                available: Number(form.available.value),
                occupied: Number(form.occupied.value),
                reserved: Number(form.reserved.value),
                maintenance: form.maintenance.checked,
                features: Array.from(form.features).filter(f => f.checked).map(f => f.value),
                images: [form.image.value],
                bookings: editRoom ? editRoom.bookings : []
              };
              handleSaveRoom(newRoom);
            }}>
              <input name="name" defaultValue={editRoom?.name || ''} placeholder="Room Name" className="border p-2 rounded w-full mb-2" required />
              <input name="beds" defaultValue={editRoom?.beds || ''} placeholder="Beds" className="border p-2 rounded w-full mb-2" required />
              <input name="capacity" defaultValue={editRoom?.capacity || ''} placeholder="Capacity" className="border p-2 rounded w-full mb-2" required />
              <input name="price" type="number" defaultValue={editRoom?.price || ''} placeholder="Price" className="border p-2 rounded w-full mb-2" required />
              <input name="available" type="number" defaultValue={editRoom?.available || 0} placeholder="Available" className="border p-2 rounded w-full mb-2" required />
              <input name="occupied" type="number" defaultValue={editRoom?.occupied || 0} placeholder="Occupied" className="border p-2 rounded w-full mb-2" required />
              <input name="reserved" type="number" defaultValue={editRoom?.reserved || 0} placeholder="Reserved" className="border p-2 rounded w-full mb-2" required />
              <div className="flex items-center mb-2">
                <input name="maintenance" type="checkbox" defaultChecked={editRoom?.maintenance} className="mr-2" />
                <span>Under Maintenance</span>
              </div>
              <div className="mb-2">
                <div className="font-semibold text-sm mb-1">Amenities:</div>
                <div className="flex flex-wrap gap-2">
                  {amenitiesList.map(a => (
                    <label key={a} className="flex items-center gap-1">
                      <input type="checkbox" name="features" value={a} defaultChecked={editRoom?.features?.includes(a)} />
                      <span>{a}</span>
                    </label>
                  ))}
                </div>
              </div>
              <input name="image" defaultValue={editRoom?.images?.[0] || ''} placeholder="Image URL" className="border p-2 rounded w-full mb-2" required />
              <button type="submit" className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded w-full">Save</button>
            </form>
          </div>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {showDelete && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setShowDelete(null)}>
          <div className="bg-white text-black rounded-lg p-8 w-full max-w-md relative" onClick={e => e.stopPropagation()}>
            <button className="absolute top-2 right-4 text-2xl" onClick={() => setShowDelete(null)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Delete Room</h2>
            <p>Are you sure you want to delete <b>{showDelete.name}</b>?</p>
            <div className="flex gap-4 mt-6">
              <button className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded" onClick={() => { setRooms(rooms.filter(r => r.id !== showDelete.id)); setShowDelete(null); }}>Delete</button>
              <button className="bg-gray-400 px-4 py-2 rounded" onClick={() => setShowDelete(null)}>Cancel</button>
            </div>
          </div>
        </div>
      )}

      {/* Booking History Modal */}
      {showBookings && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setShowBookings(null)}>
          <div className="bg-white text-black rounded-lg p-8 w-full max-w-md relative" onClick={e => e.stopPropagation()}>
            <button className="absolute top-2 right-4 text-2xl" onClick={() => setShowBookings(null)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Booking History for {showBookings.name}</h2>
            {showBookings.bookings.length === 0 ? <p>No bookings yet.</p> : (
              <ul className="list-disc list-inside">
                {showBookings.bookings.map((b, i) => <li key={i}>{b.guest} - {b.date}</li>)}
              </ul>
            )}
          </div>
        </div>
      )}

      {/* Image Gallery Modal */}
      {showGallery && (
        <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50" onClick={() => setShowGallery(null)}>
          <div className="bg-white text-black rounded-lg p-8 w-full max-w-2xl relative" onClick={e => e.stopPropagation()}>
            <button className="absolute top-2 right-4 text-2xl" onClick={() => setShowGallery(null)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Images for {showGallery.name}</h2>
            <div className="flex gap-4 flex-wrap">
              {showGallery.images.map((img, i) => (
                <img key={i} src={img} alt={showGallery.name + i} className="h-40 w-60 object-cover rounded" />
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Import Modal (placeholder) */}
      {importModal && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setImportModal(false)}>
          <div className="bg-white text-black rounded-lg p-8 w-full max-w-md relative" onClick={e => e.stopPropagation()}>
            <button className="absolute top-2 right-4 text-2xl" onClick={() => setImportModal(false)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Import Rooms (CSV)</h2>
            <p className="mb-4">This is a placeholder for CSV import functionality.</p>
            <input type="file" className="mb-4" />
            <button className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded w-full" onClick={() => setImportModal(false)}>Import</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Rooms;
