import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';
import Rooms from './Rooms.jsx';
import Guests from './Guests.jsx';
import FrontDesk from './FrontDesk';
// import Rate from './Rate';
import Bookings from './Bookings';
// import Settings from './Settings';
// import Help from './Help';
import { dashboardApi } from './lib/supabase';
import './Navbar.css';

const sidebarItems = [
  { name: 'Dashboard', icon: 'üè†', path: '/' },
  { name: 'Rooms', icon: 'üõèÔ∏è', path: '/rooms' },
  { name: 'Guests', icon: 'üë§', path: '/guests' },
  { name: 'Front Desk', icon: 'üõéÔ∏è', path: '/front-desk' },
  // { name: 'Rate', icon: 'üí≤', path: '/rate' },
  { name: 'Bookings', icon: 'üìñ', path: '/bookings' },
  // { name: 'Settings', icon: '‚öôÔ∏è', path: '/settings' },
  // { name: 'Help', icon: '‚ùì', path: '/help' },
];

function Dashboard() {
  const [stats, setStats] = useState({
    todayCheckIns: 0,
    todayCheckOuts: 0,
    totalAvailableRooms: 0,
    totalOccupiedRooms: 0,
    checkInChange: '+0',
    checkOutChange: '+0',
    availableChange: '0',
    occupiedChange: '0'
  });
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        setLoading(true);
        const [statsData, roomsData] = await Promise.all([
          dashboardApi.getDashboardStats(),
          dashboardApi.getDashboardRooms()
        ]);
        setStats(statsData);
        setRooms(roomsData);
        setError(null);
      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError('Failed to load dashboard data');
      } finally {
        setLoading(false);
      }
    };

    fetchDashboardData();

    // Refresh data every 30 seconds
    const interval = setInterval(fetchDashboardData, 30000);
    return () => clearInterval(interval);
  }, []);

  if (loading) {
    return (
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
        </div>
      </main>
    );
  }

  if (error) {
    return (
      <main className="flex-1 p-8 overflow-y-auto">
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded">
          {error}
        </div>
      </main>
    );
  }

  return (
    <main className="flex-1 p-8 overflow-y-auto">
      {/* Overview Cards */}
      <section className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Overview</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          {[
            { title: "Today's Check-in", value: stats.todayCheckIns, change: `${stats.checkInChange} from yesterday`, icon: '‚û°Ô∏è' },
            { title: "Today's Check-out", value: stats.todayCheckOuts, change: `${stats.checkOutChange} from yesterday`, icon: '‚û°Ô∏è' },
