import React, { useState } from 'react';

const initialRates = [
  { roomType: 'Family Suite', rate: 3300 },
  { roomType: 'Junior Suite', rate: 2200 },
  { roomType: 'Twin Superior', rate: 1600 },
  { roomType: 'Double Superior', rate: 1500 },
  { roomType: 'Studio Room', rate: 1400 },
];

function Rate() {
  const [rates, setRates] = useState(initialRates);
  const [editIdx, setEditIdx] = useState(null);
  const [editValue, setEditValue] = useState('');
  const [showAdd, setShowAdd] = useState(false);
  const [newRoom, setNewRoom] = useState({ roomType: '', rate: '' });

  const handleEdit = (idx) => {
    setEditIdx(idx);
    setEditValue(rates[idx].rate);
  };

  const handleSave = (idx) => {
    const updated = [...rates];
    updated[idx].rate = Number(editValue);
    setRates(updated);
    setEditIdx(null);
    setEditValue('');
  };

  const handleDelete = (idx) => {
    setRates(rates.filter((_, i) => i !== idx));
  };

  const handleAddRoom = () => {
    if (!newRoom.roomType.trim() || !newRoom.rate) return;
    setRates([{ roomType: newRoom.roomType, rate: Number(newRoom.rate) }, ...rates]);
    setShowAdd(false);
    setNewRoom({ roomType: '', rate: '' });
  };

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold mb-6">Room Rate Management</h2>
      <div className="mb-4 flex justify-end">
        <button className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded" onClick={() => setShowAdd(true)}>Add Room Type</button>
      </div>
      <div className="max-w-xl mx-auto bg-[#232f47] rounded-lg shadow-lg p-6">
        <table className="w-full text-left text-white">
          <thead>
            <tr>
              <th className="py-2 px-4">Room Type</th>
              <th className="py-2 px-4">Rate (₱/night)</th>
              <th className="py-2 px-4">Actions</th>
            </tr>
          </thead>
          <tbody>
            {rates.map((row, idx) => (
              <tr key={row.roomType} className="border-t border-[#1a253a]">
                <td className="py-2 px-4 font-semibold">{row.roomType}</td>
                <td className="py-2 px-4">
                  {editIdx === idx ? (
                    <input
                      type="number"
                      className="bg-[#151e2e] text-white rounded px-2 py-1 w-32"
                      value={editValue}
                      onChange={e => setEditValue(e.target.value)}
                    />
                  ) : (
                    `₱${row.rate.toLocaleString()}`
                  )}
                </td>
                <td className="py-2 px-4 flex gap-2">
                  {editIdx === idx ? (
                    <>
                      <button className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded" onClick={() => handleSave(idx)}>Save</button>
                      <button className="bg-gray-400 px-3 py-1 rounded" onClick={() => setEditIdx(null)}>Cancel</button>
                    </>
                  ) : (
                    <>
                      <button className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded" onClick={() => handleEdit(idx)}>Edit</button>
                      <button className="bg-red-600 hover:bg-red-700 text-white px-3 py-1 rounded" onClick={() => handleDelete(idx)}>Delete</button>
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {/* Add Room Modal */}
      {showAdd && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setShowAdd(false)}>
          <div className="bg-white text-black rounded-lg p-8 w-full max-w-md relative" onClick={e => e.stopPropagation()}>
            <button className="absolute top-2 right-4 text-2xl" onClick={() => setShowAdd(false)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Add Room Type</h2>
            <form onSubmit={e => { e.preventDefault(); handleAddRoom(); }} className="flex flex-col gap-4">
              <div>
                <label className="block text-sm font-semibold mb-1">Room Type</label>
                <input
                  className="border p-2 rounded w-full"
                  value={newRoom.roomType}
                  onChange={e => setNewRoom({ ...newRoom, roomType: e.target.value })}
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-semibold mb-1">Rate (₱/night)</label>
                <input
                  type="number"
                  className="border p-2 rounded w-full"
                  value={newRoom.rate}
                  onChange={e => setNewRoom({ ...newRoom, rate: e.target.value })}
                  required
                />
              </div>
              <div className="flex justify-end gap-2 mt-2">
                <button type="button" className="bg-gray-400 px-4 py-2 rounded" onClick={() => setShowAdd(false)}>Cancel</button>
                <button type="submit" className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded">Add</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Rate;
