    pax: '',
    amount: '',
    remarks: 'Checked-Out',
    sector: 'TELECOM IT',
    purpose: 'Business & related business/trade',
    travel: 'Repeat visitors',
    frequency: 'Repeat visitors',
    gender: 'FEMALE',
    type: 'Domestic Guest',
    address: 'BAGUIO CITY',
    checkedInBy: 'RENDEL',
    checkedOutBy: 'MAYBELL',
    plateNo: ''
  },
  {
    checkInDate: 'Mar 30',
    checkInTime: '2:34:00 AM',
    checkOutDate: 'Apr 2',
    checkOutTime: '11:26 AM',
    name: '',
    company: '',
    email: '',
    contact: '9364257542',
    roomNo: '20',
    days: '3',
    pax: '7',
    amount: '9800',
