  },
  {
    checkInDate: 'Mar 30',
    checkInTime: '12 nn',
    checkOutDate: 'Mar 30',
    checkOutTime: '',
    name: '',
    company: '',
    email: '',
    contact: '',
    roomNo: '21',
    days: '1',
    pax: '1',
    amount: '1500',
    remarks: 'Check -Inn',
    sector: '',
