import React, { useState, useMemo } from 'react';

const initialEmployees = ['Alice', 'Bob', 'Carol', 'Dave', 'Eve'];

function getDaysInMonth(year, month) {
  return new Date(year, month + 1, 0).getDate();
}

function getMonthMatrix(year, month) {
  const daysInMonth = getDaysInMonth(year, month);
  const firstDay = new Date(year, month, 1).getDay();
  const matrix = [];
  let week = Array(firstDay).fill(null);
  for (let day = 1; day <= daysInMonth; day++) {
    week.push(day);
    if (week.length === 7) {
      matrix.push(week);
      week = [];
    }
  }
  if (week.length) {
    while (week.length < 7) week.push(null);
    matrix.push(week);
  }
  return matrix;
}

const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

function FrontDesk() {
  const today = new Date();
  const [year, setYear] = useState(today.getFullYear());
  const [month, setMonth] = useState(today.getMonth());
  const [employees, setEmployees] = useState(initialEmployees);
  // Multiple assigned employees per day (client-side only)
  const [assignments, setAssignments] = useState(() => {
    let empIdx = 0;
    const a = [];
    for (let day = 1; day <= getDaysInMonth(year, month); day++) {
      a[day] = [employees[empIdx % employees.length]];
      empIdx++;
    }
    return a;
  });
  const [showAddEmp, setShowAddEmp] = useState(false);
  const [newEmp, setNewEmp] = useState('');
  const [showEdit, setShowEdit] = useState(null); // day number
  const [editEmp, setEditEmp] = useState('');
  const [showDeleteEmp, setShowDeleteEmp] = useState(null);

  // Drawer state (no DB)
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [selectedDay, setSelectedDay] = useState(null);
  const [pickerEmp, setPickerEmp] = useState(initialEmployees[0]);

  // Update assignments when month/year/employees change
  React.useEffect(() => {
    let empIdx = 0;
    const a = [];
    for (let day = 1; day <= getDaysInMonth(year, month); day++) {
      a[day] = [employees[empIdx % employees.length]];
      empIdx++;
    }
    setAssignments(a);
    // eslint-disable-next-line
  }, [year, month, employees.length]);

  const daysMatrix = useMemo(() => getMonthMatrix(year, month), [year, month]);
  const monthName = useMemo(() => new Date(year, month).toLocaleString('default', { month: 'long' }), [year, month]);

  const handlePrevMonth = () => {
    if (month === 0) {
      setMonth(11);
      setYear(y => y - 1);
    } else {
      setMonth(m => m - 1);
    }
  };
  const handleNextMonth = () => {
    if (month === 11) {
      setMonth(0);
      setYear(y => y + 1);
    } else {
      setMonth(m => m + 1);
    }
  };

  const handleAddEmp = () => {
    if (newEmp.trim() && !employees.includes(newEmp.trim())) {
      setEmployees([...employees, newEmp.trim()]);
      setNewEmp('');
      setShowAddEmp(false);
    }
  };

  const handleEditAssignment = (day) => {
    if (editEmp && employees.includes(editEmp)) {
      setAssignments(a => {
        const copy = [...a];
        copy[day] = editEmp;
        return copy;
      });
      setShowEdit(null);
      setEditEmp('');
    }
  };

  const handleDeleteEmp = (emp) => {
    setEmployees(emps => emps.filter(e => e !== emp));
    setAssignments(a => a.map(list => Array.isArray(list) ? list.filter(name => name !== emp) : list));
    setShowDeleteEmp(null);
  };

  // Drawer helpers
  const openDrawer = (day) => { setSelectedDay(day); setDrawerOpen(true); };
  const closeDrawer = () => setDrawerOpen(false);
  const assignEmployeeToSelected = () => {
    if (!selectedDay) return;
    setAssignments(a => {
      const copy = [...a];
      const list = Array.isArray(copy[selectedDay]) ? copy[selectedDay] : [];
      if (!list.includes(pickerEmp)) list.push(pickerEmp);
      copy[selectedDay] = [...list];
      return copy;
    });
  };
  const clearAssignmentsForSelected = () => {
    if (!selectedDay) return;
    setAssignments(a => {
      const copy = [...a];
      copy[selectedDay] = [];
      return copy;
    });
  };

  const removeAssignmentForSelected = (emp) => {
    if (!selectedDay) return;
    setAssignments(a => {
      const copy = [...a];
      copy[selectedDay] = (copy[selectedDay] || []).filter(e => e !== emp);
      return copy;
    });
  };

  return (
    <div className="p-8">
      <h2 className="text-2xl font-bold mb-6">Front Desk Duty Calendar</h2>
      <div className="max-w-5xl mx-auto bg-[#232f47] rounded-lg shadow-lg p-6">
        <div className="flex justify-between items-center mb-4">
          <button className="bg-gray-600 hover:bg-gray-700 text-white px-3 py-1 rounded" onClick={handlePrevMonth}>&lt;</button>
          <span className="text-xl font-semibold">{monthName} {year}</span>
          <button className="bg-gray-600 hover:bg-gray-700 text-white px-3 py-1 rounded" onClick={handleNextMonth}>&gt;</button>
        </div>
        <div className="grid grid-cols-7 gap-2 text-center mb-2">
          {weekDays.map(d => (
            <div key={d} className="font-semibold text-blue-300">{d}</div>
          ))}
        </div>
        <div className="grid grid-cols-7 gap-2">
          {daysMatrix.flat().map((day, idx) => (
            <div
              key={idx}
              className={`h-20 rounded flex flex-col items-center justify-center ${day ? 'bg-[#1a253a] text-white cursor-pointer hover:bg-[#223150]' : ''}`}
              style={{ opacity: day ? 1 : 0 }}
              onClick={() => day && openDrawer(day)}
            >
              {day && (
                <>
                  <div className="text-lg font-bold">{day}</div>
                  {Array.isArray(assignments[day]) && assignments[day].length > 0 && (
                    <div className="mt-2 text-[10px] leading-4 text-white flex items-center gap-1 flex-wrap justify-center">
                      {assignments[day].slice(0, 2).map(name => (
                        <span key={name} className="bg-blue-600 rounded px-2 py-0.5">{name}</span>
                      ))}
                      {assignments[day].length > 2 && (
                        <span className="bg-blue-800 rounded px-2 py-0.5">+{assignments[day].length - 2} more</span>
                      )}
                    </div>
                  )}
                </>
              )}
            </div>
          ))}
        </div>
        <div className="mt-6">
          <h3 className="font-semibold mb-2 text-blue-300">Employee Legend:</h3>
          <div className="flex gap-4 flex-wrap items-center">
            {employees.map(e => (
              <span key={e} className="bg-blue-600 text-white px-3 py-1 rounded text-sm flex items-center gap-2">
                {e}
                <button className="bg-red-500 text-white px-1 rounded text-xs" onClick={() => setShowDeleteEmp(e)} title="Delete"></button>
              </span>
            ))}
            <button className="bg-green-600 hover:bg-green-700 text-white px-3 py-1 rounded text-sm" onClick={() => setShowAddEmp(true)}>+ Add Employee</button>
          </div>
        </div>
      </div>

      {/* Right-side Drawer */}
      {drawerOpen && (
        <div className="fixed inset-0 z-50">
          <div className="absolute inset-0 bg-black bg-opacity-40" onClick={closeDrawer} />
          <div className="absolute right-0 top-0 h-full w-full sm:w-[420px] bg-white text-gray-900 shadow-2xl p-6 overflow-y-auto">
            <div className="flex items-center justify-between mb-4">
              <div className="text-lg font-semibold"> {monthName} {selectedDay}, {year}</div>
              <button className="text-2xl leading-none" onClick={closeDrawer}>&times;</button>
            </div>

            <div className="mb-4">
              <div className="font-semibold mb-2"> Assigned Employees</div>
              <div className="border rounded-lg divide-y">
                {(!assignments[selectedDay] || assignments[selectedDay].length === 0) && (
                  <div className="p-3 text-sm text-gray-500">No one assigned yet.</div>
                )}
                {Array.isArray(assignments[selectedDay]) && assignments[selectedDay].map(emp => (
                  <div key={emp} className="flex items-center justify-between p-3">
                    <div>
                      <div className="font-medium">{emp}</div>
                      <div className="text-xs text-gray-500">Assigned</div>
                    </div>
                    <button className="text-red-600 hover:text-red-700 text-sm" onClick={() => removeAssignmentForSelected(emp)} title="Remove">Remove</button>
                  </div>
                ))}
              </div>
              <div className="mt-3 flex gap-2">
                <select className="border rounded px-2 py-1" value={pickerEmp} onChange={e => setPickerEmp(e.target.value)}>
                  {employees.map(e => <option key={e} value={e}>{e}</option>)}
                </select>
                <button className="bg-blue-600 text-white px-3 py-1 rounded" onClick={assignEmployeeToSelected}>+ Add Employee</button>
              </div>
              <div className="mt-3">
                <button className="text-sm text-red-600 hover:text-red-700" onClick={clearAssignmentsForSelected}> Remove All</button>
              </div>
            </div>

          </div>
        </div>
      )}

      {/* Add Employee Modal */}
      {showAddEmp && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setShowAddEmp(false)}>
          <div className="bg-white text-black rounded-lg p-8 w-full max-w-md relative" onClick={e => e.stopPropagation()}>
            <button className="absolute top-2 right-4 text-2xl" onClick={() => setShowAddEmp(false)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Add Employee</h2>
            <input
              className="border p-2 rounded w-full mb-4"
              value={newEmp}
              onChange={e => setNewEmp(e.target.value)}
              placeholder="Employee Name"
            />
            <div className="flex justify-end gap-2">
              <button className="bg-gray-400 px-4 py-2 rounded" onClick={() => setShowAddEmp(false)}>Cancel</button>
              <button className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded" onClick={handleAddEmp}>Add</button>
            </div>
          </div>
        </div>
      )}
      {/* Edit Assignment Modal */}
      {showEdit && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setShowEdit(null)}>
          <div className="bg-white text-black rounded-lg p-8 w-full max-w-md relative" onClick={e => e.stopPropagation()}>
            <button className="absolute top-2 right-4 text-2xl" onClick={() => setShowEdit(null)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Edit Assignment for Day {showEdit}</h2>
            <select
              className="border p-2 rounded w-full mb-4"
              value={editEmp}
              onChange={e => setEditEmp(e.target.value)}
            >
              {employees.map(e => (
                <option key={e} value={e}>{e}</option>
              ))}
            </select>
            <div className="flex justify-end gap-2">
              <button className="bg-gray-400 px-4 py-2 rounded" onClick={() => setShowEdit(null)}>Cancel</button>
              <button className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded" onClick={() => handleEditAssignment(showEdit)}>Save</button>
            </div>
          </div>
        </div>
      )}
      {/* Delete Employee Modal */}
      {showDeleteEmp && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" onClick={() => setShowDeleteEmp(null)}>
          <div className="bg-white text-black rounded-lg p-8 w-full max-w-md relative" onClick={e => e.stopPropagation()}>
            <button className="absolute top-2 right-4 text-2xl" onClick={() => setShowDeleteEmp(null)}>&times;</button>
            <h2 className="text-xl font-bold mb-4">Delete Employee</h2>
            <p>Are you sure you want to delete <b>{showDeleteEmp}</b>?</p>
            <div className="flex justify-end gap-2 mt-4">
              <button className="bg-gray-400 px-4 py-2 rounded" onClick={() => setShowDeleteEmp(null)}>Cancel</button>
              <button className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded" onClick={() => handleDeleteEmp(showDeleteEmp)}>Delete</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default FrontDesk;
