import React, { useState, useEffect, useRef } from 'react';
import { Routes, Route, NavLink, Navigate } from 'react-router-dom';
import Rooms from './Rooms.jsx';
import Reports from './Reports.jsx';
import OverviewCalendar from './OverviewCalendar.jsx';
import Bookings from './Bookings';
import Customers from './Customers.jsx';
import { supabase, adminAuth } from './lib/supabase';
import Login from './Login.jsx';
import Dashboard from './Dashboard.jsx';
import AdminManagement from './AdminManagement';
import './Navbar.css';

const sidebarItems = [
  { name: 'Dashboard', icon: 'ðŸ ', path: '/' },
  { name: 'Rooms', icon: 'ðŸ›ï¸', path: '/rooms' },
  { name: 'Bookings', icon: 'ðŸ“–', path: '/bookings' },
  { name: 'Overview Calendar', icon: 'ðŸ“…', path: '/overview-calendar' },
  { name: 'Customers', icon: 'ðŸ‘¥', path: '/customers' },
  { name: 'Reports', icon: 'ðŸ“Š', path: '/reports' },
  { name: 'Admin Management', icon: 'ðŸ‘¨â€ðŸ’¼', path: '/admin-management' },
];

function App() {
  const [profileOpen, setProfileOpen] = useState(false);
  const profileRef = useRef(null);
  const [authed, setAuthed] = useState(!!adminAuth.getToken());
  const [adminUser, setAdminUser] = useState(() => adminAuth.getUser());

  useEffect(() => {
    // Listen for login/logout events and focus/storage to refresh auth state
    const onAuthChange = () => {
      setAuthed(!!adminAuth.getToken());
      setAdminUser(adminAuth.getUser());
    };
    window.addEventListener('admin-auth-changed', onAuthChange);
    window.addEventListener('storage', onAuthChange);
    window.addEventListener('focus', onAuthChange);
    return () => {
      window.removeEventListener('admin-auth-changed', onAuthChange);
      window.removeEventListener('storage', onAuthChange);
      window.removeEventListener('focus', onAuthChange);
    };
  }, []);

  useEffect(() => {
    // Close dropdown on outside click
    const onClick = (e) => {
      if (profileRef.current && !profileRef.current.contains(e.target)) {
        setProfileOpen(false);
      }
    };
    document.addEventListener('click', onClick);
    return () => document.removeEventListener('click', onClick);
  }, []);

  const toggleProfile = () => {
    setProfileOpen((o) => !o);
  };

  const handleLogout = async () => {
    try {
      adminAuth.logout();
      window.location.href = '/login';
    } catch (e) {
      alert('Failed to logout');
    }
  };

  // If not authenticated, only allow access to /login
  if (!authed) {
    return (
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    );
  }

  // Determine role and build sidebar items accordingly
  const role = (adminUser?.role || (adminUser?.username === 'owner' ? 'owner' : 'admin')).toLowerCase();
  const isOwner = role === 'owner' || adminUser?.username === 'owner';
  const visibleSidebarItems = isOwner
    ? sidebarItems
    : sidebarItems.filter(item => item.path !== '/admin-management');

  return (
    <div className="flex min-h-screen bg-[#151e2e] text-white">
        {/* Sidebar */}
        <aside className="sidebar">
          <div className="sidebar-logo">
            <div>
              <div className="sidebar-logo-title">GOLD ROCK</div>
              <div className="sidebar-logo-sub">INN</div>
            </div>
          </div>
          <nav className="sidebar-nav">
            {visibleSidebarItems.map((item) => (
              <NavLink
                key={item.name}
                to={item.path}
                end={item.path === '/'}
                className={({ isActive }) =>
                  `sidebar-link ${isActive ? 'sidebar-link-active' : ''}`
                }
              >
                <span className="sidebar-link-icon">{item.icon}</span>
                <span>{item.name}</span>
              </NavLink>
            ))}
          </nav>
        </aside>

        {/* Main Content */}
        <div className="flex-1 flex flex-col">
          {/* Topbar */}
          <header className="topbar">
            <div className="topbar-title">
              <span>Gold Rock Inn</span>
              <div className="flex items-center gap-2">
                <span className="topbar-username">{adminUser?.username || 'User'}</span>
                <span className="topbar-role">({role})</span>
              </div>
            </div>
            <div className="topbar-actions">
                {/* Profile */}
              <div className="topbar-icon relative" ref={profileRef} onClick={toggleProfile} title="Profile">
                <span className="avatar">
                  <span className="avatar-initial">
                    {(adminUser?.full_name?.[0] || adminUser?.username?.[0] || 'A').toUpperCase()}
                  </span>
                </span>
                {profileOpen && (
                  <div className="dropdown-menu right">
                    <button className="dropdown-action highlight" onClick={(e) => { e.stopPropagation(); alert('Profile page coming soon'); setProfileOpen(false); }}>Profile</button>
                    <button className="dropdown-action danger" onClick={(e) => { e.stopPropagation(); handleLogout(); }}>Logout</button>
                  </div>
                )}
              </div>
            </div>
          </header>

          {/* Main Content */}
          <main className="flex-1 p-6 overflow-auto">
            <Routes>
              <Route path="/" element={<Dashboard />} />
              <Route path="/rooms" element={<Rooms />} />
              <Route path="/bookings" element={<Bookings />} />
              <Route path="/overview-calendar" element={<OverviewCalendar />} />
              <Route path="/customers" element={<Customers />} />
              <Route path="/reports" element={<Reports />} />
              <Route path="/admin-management" element={isOwner ? <AdminManagement /> : <Navigate to="/" replace />} />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </main>
        </div>
    </div>
  );
}

export default App;
